member.summary:
Additional JSON configuration files
e.g. Config_Init server event
JsonFiles.Add(new { Path = "foobar.json", Optional = false, ReloadOnChange = true });

member.param:
Options, e.g. new Dictionary<string, List<object>> {
	{"lv1", new List<object> {"lv1", "dv", "dv2", "dv3", "dv4"}},
	{"lv2", new List<object> {"lv2", "dv", "dv2", "dv3", "dv4"}}
}

Options, e.g. new List<List<object>> {
	new List<object> {"1", "option1", "df1", "df2", "df3" },
	new List<object> {"2", "option2", "df1", "df2" },
	new List<object> {"3", "option3", "df1", "df3", "df3a" },
}

Options, e.g. new List<object[]> {
	new object[] {"1", "option1", "df1", "df2", "df3" },
	new object[] {"2", "option2", "df1", "df2" },
	new object[] {"3", "option3", "df1", "df3", "df3a" },
}
